mapper.select:
select favorite.favorite_no, favorite.store_no, store.thumbnail, store.name, store.address_short, store.class_first, store.payback_rate from favorite, store WHERE favorite.user_no = #{user_no} AND store.store_no = favorite.store_no
AND store.class_first = #{class_first}
ORDER BY favorite.reg_date DESC, favorite_no DESC LIMIT 10

SET @RegDate = (select reg_date from favorite WHERE favorite_no = #{last_index}); select favorite.favorite_no, favorite.store_no, store.thumbnail, store.name, store.address_short, store.class_first, store.payback_rate from favorite, store WHERE favorite.user_no = #{user_no} AND store.store_no = favorite.store_no
AND store.class_first = #{class_first}
AND (favorite.reg_date < @RegDate OR (favorite.reg_date = @RegDate AND favorite_no < #{last_index})) ORDER BY favorite.reg_date DESC, favorite_no DESC LIMIT 10
