mapper.select:
select vendor_no, valid_vendor, vendor_penalty
from vendor
WHERE id = #{id}
  AND password = #{password}

select vendor_penalty
from vendor
WHERE vendor_no = #{vendor_no}

select vendor_name
from vendor
WHERE vendor_no = #{vendor_no}

select name, point, vendor.vendor_no as vendor_no, vendor_name
from store,
     vendor
WHERE store.vendor_no = #{vendor_no}
  AND vendor.vendor_no = #{vendor_no}

select vendor_no, point
from vendor
WHERE vendor_no = #{vendor_no}

mapper.update:
update vendor
SET point = point - #{point}
WHERE vendor_no = #{vendor_no}

mapper.select:
select fcm_token
from vendor
WHERE vendor_no = #{vendor_no}

mapper.update:
update vendor
SET fcm_token = #{fcm_token}
WHERE vendor_no = #{vendor_no}

mapper.select:
select id
from vendor
WHERE vendor_no = #{vendor_no}

select vendor_no
from vendor
WHERE vendor_name = #{name}
  AND phone = #{phone}

select vendor_no, id
from vendor
WHERE vendor_no = #{vendor_no}

select IF(password = #{password}, TRUE, FALSE)
from vendor
WHERE vendor_no = #{vendor_no}

mapper.update:
update vendor
SET password = #{password}
WHERE vendor_no = #{vendor_no}

mapper.select:
select id, vendor_name as name, phone, bank_name, bank_account, bank_owner
from vendor
WHERE vendor_no = #{vendor_no}

select store.name as store_name, vendor.business_code, vendor.vendor_name
from store,
     vendor
WHERE vendor.vendor_no = #{vendor_no}
  AND store.vendor_no = #{vendor_no}

mapper.update:
update vendor
SET fcm_token = #{fcm_token}
WHERE vendor_no = #{vendor_no}

mapper.select:
select COUNT(*)
from point_charge
WHERE vendor_no = #{vendor_no}

select point
from vendor
WHERE vendor_no = #{vendor_no}

select COUNT(*)
from faq
WHERE type = #{type}

mapper.update:
update vendor
SET vendor_name = #{name},
    phone       = #{phone}
WHERE vendor_no = #{vendor_no}

update vendor
SET bank_name    = #{bankName},
    bank_account = #{bankAccount},
    bank_owner   = #{bankOwner}
WHERE vendor_no = #{vendor_no}

update vendor, store
SET vendor.vendor_name = #{vendorName},
    store.name         = #{storeName}
WHERE vendor.vendor_no = #{vendor_no}
  AND store.vendor_no = #{vendor_no}

mapper.select:
select room_options -> '$[*].room_no'
from store_lodgement
WHERE store_no = #{store_no}

select store_no
from store
WHERE vendor_no = #{vendor_no}

select charge_no, point, reg_date, revise_date, status from point_charge WHERE vendor_no = #{vendor_no}
AND status = 2 AND status = 3 AND (status = 1 OR status = 4) AND status = 5
ORDER BY reg_date DESC LIMIT #{start_index}, 10

select point_charge.point, point_charge.reg_date as request_date, point_charge_reject.reason as reject_reason
from point_charge,
     point_charge_reject
WHERE point_charge.charge_no = #{charge_no}
  AND point_charge_reject.charge_no = #{charge_no}

SET @CancelReason = (SELECT reason
                     from point_charge_cancel
                     WHERE charge_no = #{charge_no});
select point, reg_date as request_date, @CancelReason as cancel_reason
from point_charge
WHERE charge_no = #{charge_no}

select status
from point_charge
WHERE charge_no = #{charge_no}

select point_accumulate.point,
       point_accumulate.reg_date      as request_date,
       point_accumulate_cancel.reason as cancel_reason,
       point_accumulate_reject.reason as reject_reason
from point_accumulate,
     point_accumulate_cancel,
     point_accumulate_reject
WHERE point_accumulate.accumulate_no = #{accumulate_no}
  AND point_accumulate_cancel.accumulate_no = #{accumulate_no}
  AND point_accumulate_reject.accumulate_no = #{accumulate_no}

select user.nickname as user_name, point_accumulate.point, point_accumulate.reg_date
from point_accumulate,
     user
WHERE point_accumulate.accumulate_no = #{accumulate_no}
  AND user.user_no = point_accumulate.user_no

select IF(status = 3, false, true)
from point_accumulate
WHERE accumulate_no = #{accumulate_no}

mapper.update:
update point_accumulate
SET status = 2
WHERE accumulate_no = #{accumulate_no}

mapper.select:
select IF(status = 1, true, false)
from point_accumulate
WHERE accumulate_no = #{accumulate_no}

mapper.update:
update point_accumulate
SET status = 3
WHERE accumulate_no = #{accumulate_no}

update review
SET answer_content = #{answer},
    answer_date    = #{reg_date},
    answer_status  = true
WHERE accumulate_no = #{accumulate_no};

update point_accumulate
SET answer_date = #{reg_date}
WHERE accumulate_no = #{accumulate_no}

update notice
SET view_num = view_num + 1
WHERE notice_no = #{notice_no}

mapper.select:
select title, content, img, reg_date
from notice
WHERE notice_no = #{notice_no}

mapper.update:
update faq
SET view_num = view_num + 1
WHERE faq_no = #{faq_no}

mapper.select:
select question, answer, img, type, reg_date
from faq
WHERE faq_no = #{faq_no}

mapper.update:
SET @StoreNo = (select store_no
                from store
                WHERE vendor_no = #{vendor_no});
update store
SET `explain` = #{explain}
WHERE store_no = @StoreNo

update store_service
SET title  = #{title},
    content= #{content}
WHERE service_no = #{service_no}

update store_information
SET title  = #{title},
    content= #{content}
WHERE information_no = #{information_no}
